mapper.select:
SELECT q.qna_no, q.title, q.content, q.end_status, q.exposure_status, q.fixed_status, q.create_dt, CASE WHEN m.member_id IS NOT NULL THEN m.member_id ELSE '*****' END AS member_id FROM tb_qna q LEFT JOIN tb_member m ON q.member_no = m.member_no
title LIKE CONCAT('%', #{page.keyword}, '%') OR content LIKE CONCAT('%', #{page.keyword}, '%')
ORDER BY q.create_dt desc LIMIT #{page.size} OFFSET #{page.offset}
