mapper.select:
select * ,
       (select file_path from tb_robot_file where robot_no = r.robot_no and thumnail_yn = 'Y' limit 1) as thumbnail_url
from tb_robot r
where board_type_1 = #{boardType1}
  and board_type_2 = #{boardType2}
  and del_yn = 'N'

  and category_no = #{categoryNo}

  and lang = #{lang}
order by robot_no;

select * ,
(select file_path from tb_robot_file where robot_no = r.robot_no and thumnail_yn = 'Y' limit 1) as thumbnail_url
from tb_robot r
where board_type_1 = #{boardType1}
and board_type_2 = #{boardType2}
and del_yn = 'N'

  and category_no = #{categoryNo}

and lang = #{lang}
order by robot_no;
